import { useState, useEffect } from 'react';
import './App.css';
import AtividadeForm from './components/AtividadeForm';
import AtividadeLista from './components/AtividadeLista';

//Math.max.apply(Math, atividades.map(item => item.id)) + 1,

let initialState = [
  {
    id: 1,
    titulo: "Primeira Atividade",
    descricao: "Primeira Atividade",
    prioridade: "1"
  },
  {
    id: 2,
    titulo: "Segunda Atividade",
    descricao: "Segunda Atividade",
    prioridade: "1"
  },
  {
    id: 3,
    titulo: "Terceira Atividade",
    descricao: "Terceira Atividade",
    prioridade: "1"
  }
];

function App() {
  const [index, setIndex] = useState(0);
  const [atividades, setAtividades] = useState(initialState)
  const [atividade, setAtividade] = useState({})

  useEffect(() => {
    atividades.length <= 0 ? setIndex(1)
     : setIndex(Math.max.apply(Math, atividades.map(item => item.id)) + 1);
  }, [input]);

  function addAtividade(ativ) {
    setAtividades([...atividades, { ...ativ }]);
  }

  function atualizarAtividade(atividade) {
    setAtividades(atividades.map(item => item.id === atividade.id ? atividade : item));
    setAtividade({id:0});
  }

  function cancelarAtividade(){
    setAtividade({id:0});
  }

  function deletarAtividade(id) {
    const atividadesFiltradas = atividades.filter(atividade => atividade.id !== id);
    setAtividades([...atividadesFiltradas]);
  }
  function pegarAtividade(id) {
    console.log(id);
    const atividade = atividades.filter((atividade) => atividade.id === id);
    setAtividade(atividade[0]);

  }


  return (
    <>
      <AtividadeForm
        addAtividade={addAtividade}
        atualizarAtividade={atualizarAtividade}
        cancelarAtividade={cancelarAtividade}
        ativSelecionada={atividade}
        atividades={atividades} />

      <AtividadeLista
        atividades={atividades}
        deletarAtividade={deletarAtividade}
        pegarAtividade={pegarAtividade}
      />
    </>
  );
}

export default App;
